import React from "react";
import RatingQuestion from "./RatingQuestion";

const ProgressBar = () => {
  const distanceFromTopScore = () => {
    if (
      props.currentScore < props.topScore &&
      colorChanger1 < 255 &&
      firstScoreRecorded &&
      gameStarted &&
      props.currentScore > 0
    ) {
      colorChanger1 = (props.currentScore * 255) / props.topScore;
      colorChanger2 = 255 - (props.currentScore * 255) / props.topScore;
      document.querySelector(
        ".progress-mover"
      ).style = `background-color: rgb(${colorChanger2},${colorChanger1},40)`;
      widthChanger = (props.currentScore * 100) / props.topScore;
      document.querySelector(".progress-mover").style.width =
        widthChanger + "%";
      percentAchieved = Math.ceil(
        (props.currentScore * 100) / questions.length
      );
      document.querySelector("#percent-achieved").innerText =
        percentAchieved + "%";
    }
  };

  return (
    <div className="progressbar">
      <div className="progress-mover"></div>
    </div>
  );
};

export default ProgressBar;
